description:
@original ons-lazy-repeat
@category list
@tutorial react/Reference/lazy-list
@description
[en] Using this component a list with millions of items can be rendered without a drop in performance.
    It does that by "lazily" loading elements into the DOM when they come into view and
    removing items from the DOM when they are not visible.
 [/en]
[ja][/ja]
@example

  renderRow(index) {
    return (
      <ListItem key={index}>
        {'Item ' + (index + 1)}
      </ListItem>
    );
  }

  render() {
    return (
      <Page renderToolbar={() => <MyToolbar title='LazyList' />} >
        <div style={{height: 100}}>
          <LazyList
            length={1000}
            renderRow={() =>
              <ListItem key={index}>
                {'Item ' + (index + 1)}
              </ListItem>
            }
            calculateItemHeight={() => 44}
          />
        </div>
      </Page>
    );
  }
}

modifier.description:
@name modifier
@type string
@required false
@description
 [en]The appearance of the lazy list.[/en]
 [ja][/ja]

length.description:
@name length
@type number
@description
 [en]The length of the list.[/en]
 [ja][/ja]

renderRow.description:
@name renderRow
@type function
@description
